INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2119, '送子观音像', '5103428', '地址：桂林市象山区滨江路1号象山景区内', '周一-周日：8:00-17:00', ' ', ' 在象鼻山南麓云峰寺后的崖壁上，有唐代“送子观音像”一龛，已有一千多年的历史了，这也是桂林仅有的彩色摩崖造像。更准确地说，这叫“诃利谛母像”。主像为诃(he 音呵)利谛母，身着华服，头戴天冠，双手抱一小孩放膝上，端坐于莲台上，身后有圆形火焰身光及头光。主像两侧，各立女侍一人，头梳发髻，着宽袖长袍宫装。龛外左右两侧站立两小孩，双手拱揖。 ', 'guilin28', ' ', '', '');
