INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2126, '桃花岩', '5014401', '桂林市灵川县定江镇莲花村委甲宅村', '10:00-17:00', ' ', '沉浸式超级溶洞漫游奇幻秀《穿越·桃花岩》是由桂林桃花岩旅游开发有限公司与甲天下（北京）旅游开发有限公司联合打造的溶洞实景大秀。全剧共分为九幕光影大戏，力求实现艺术与科技在溶洞实景中的完美融合，以营造沉浸式“模糊现实”奇幻体验。大地的不变传奇、海洋的奇妙故事、蓬莱的奇幻仙境、飞龙的气薄云天、桃花的浪漫情缘、田园牧歌的美好记忆、远山的灵魂呼唤、冰川的神奇幻境以及星空的深邃神秘，都在这里神奇再现。  《穿越·桃花岩》以3亿年前溶洞诞生为时间跨度，在时空的叠置转环中，呈现对自然精神的呼唤，对生命灵魂的礼赞，是一曲自由生命的赞歌。 特色亮点：《穿越·桃花岩》是迄今为止全球最大的全数字化实景溶洞秀，同时也营造了全球最大的数字海底世界！其奇绝的自然奇观，瑰丽的艺术想象，必将激活你每一个探索的细胞，颠覆平凡，超越想象！ 地址：项目位于桂林市灵川县定江镇莲花村委甲宅村，桃花江上游，紧临西二环路，距离芦笛岩3公里。目前开通83路公交路线（桂林北站—临桂区政府），在甲宅村站下车。    ', 'guilin28', '', '', '');
