INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2128, '瓦窑小镇', '5119365', '地址：桂林市象山区平山北路11号', '全年 10:00-16:00。', '0', ' 桂林瓦窑小镇位于桂林城南，麒麟山脚下，是以展现桂林本土艺术品、手工匠艺、桂州窑历史文化为主题的文旅特色小镇。在麒麟山脚下规划为“匠园”景区，对接旅游观光，通过5个工坊（石雕工坊、木艺工坊、陶艺工坊、竹艺工坊、冶金工坊等）和古戏台、桂州窑文化遗产基地来展现瓦窑千年的人文历史和工匠精神。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2129, '阳朔大本营溜索速降课程', '2482880', '地址：桂林市阳朔县阳朔大本营（月亮山背后凤楼村）', '', ' ', ' 阳朔大本营距离月亮山不远，这里提供丰富多样的户外运动，有攀岩、溜索速降、定向越野、露营等。溜索速降算得上是这里最刺激的一项体验项目，游客通过索道腾空翱翔在空中，抵达下降点后，俯瞰月亮山美景，然后再安全速降30米到达地面，惊险而又刺激，同时还能纵览阳朔美景。这里所有的装备也都是经过行业认证的，教练也具有资深的背景，能让游客在安全保障下尽兴而归。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2130, '渡头沙滩车乡村之旅', '5118370', '地址：桂林市阳朔县福利镇渡头村委广场', '淡季（11月-次年5月）：8:30-17:00，旺季（6月-10月）：7:30-18:00。', ' ', ' 渡头沙滩车乡村之旅是一家具有经营许可证的沙滩车玩乐项目景区，公司现有多台全地形沙滩车。每天可接待人数100至150人。每趟骑行时间90分钟至120分钟（视游客上手情况而定）。全程与山地越野专业领队一起启程，一路体验不同的骑行乐趣。阳朔渡头沙滩车位于自然风景优美的阳朔县渡头镇，景区周边景色秀丽，宁静安谧。  ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2131, '五通镇', '4996348', '地址：桂林市临桂区五通镇', '', ' ', ' 五通镇旧名桑江镇，位于桂林市西北郊，是桂西北四大古镇之一，有义江缘景区、刘三姐茶园等旅游景点。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2132, '香草森林', '4380377', '地址：桂林市阳朔县阳朔镇矮山门村', '全年 09:00-17:00。', ' ', ' 阳朔香草森林位于阳朔遇龙河畔，在这里可以赏花容、闻花香、品花茶、叙花事；也提供台湾风味的美食佳肴，可以体验手工皂、香草布艺等diy制作，也是婚纱摄影户外婚礼的基地，主题乐园更为亲子、情侣提供了互动的好场所，在赏花涛香海、山水风景时愉快挑选自己喜欢的各种芳香产品、文创产品.....。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2133, '白龙洞', '2723217', '桂林市象山区中山南路2号', '全年 08:00-17:30。', ' ', '白龙洞是南溪山上的洞穴，民国时期曾经作为桂林市的造币厂，洞口高20米，宽13米，洞身曲折、洞壁洁白，洞口有一垂挂的石钟乳，酷似龙头，洞顶有长长的石槽则似龙迹。 相传远古曾有一条白龙隐聚此山，潜心修炼，造福白姓，终成正果。 ', 'guilin28', '', '', '');
