INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2136, '榕湖人文及美术馆', '5101441', '桂林市秀峰区榕湖北路16号榕湖饭店5号楼', '10:00-21:00', ' ', '榕湖人文及美术馆位于桂林榕湖饭店5号楼内，收藏有唐卡、紫砂壶、唐三彩、织绣、制扇、漆器、剪纸、木雕、玉雕等中华民族民间艺术中弥足珍贵的非物质文化遗产。本馆特邀非遗传承人纯手工仿真活态还原12项非物质文化遗产的创作工艺流程场景，以独特视角感受艺术珍宝的来之不易。         ', 'guilin28', '', '', '');
