INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2141, '神岭瀑布', '2474166', '地址：桂林市灵川三街镇容流村', '8:00-17:00。', ' ', ' 神岭瀑布位于灵川北部,距县城3公里,潭深、谷幽、峰奇、岩险。神岭瀑布是整个风景名胜区的重点景观。神岭因高山苍郁而显峡谷之深邃,溪涧清洌更见潭水之幽静,加上林木葱郁,鸟鸣蝉噪,飞瀑沥沥,给人以与世隔绝的桃源之感。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2142, '海浪池', '5039341', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 这里是一个充满欢乐和激情的水上世界。海啸池可以制造3米高的海啸，从出口横向前推，壮丽秀美,惊心动魄，它是国内制造浪型最高最大的设备。没错，玛雅水上乐园就是要让你体验到大海的感觉，大海的魅力! ', 'guilin28', ' ', '', '');
