INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2144, '水上过山车', '5037345', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 疯狂的水上自由落体运动，只为勇敢者设计的游戏。在高空放下自己，低处找回“全新”的自我。从六层楼高的平台，以7米/秒的速度紧贴着几乎垂直的滑道下冲，无比刺激~ ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2145, '九屋西岭温泉度假村', '5679546', '桂林市灵川县九屋镇西岭村', '全年 09:00-18:00。', ' ', '西岭温泉度假村位于桂林灵川县九屋镇西岭村，距离桂林市区仅50公里，是一个集运动、休闲、娱乐、度假为一体的综合性人文休闲旅游景区。 景区内原始森林，藤蔓缠绕，万倾竹海，苍翠欲滴，清山连绵、泉水清澈如碧，是水上游乐、周末度假、休闲避暑、观光采摘、生态旅游之胜地。 ', 'guilin28', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2146, '西南保障榜书', '5099445', '地址：桂林市象山区滨江路1号象山景区内', '周一-周日：8:00-17:00', ' ', ' 在象鼻山下山的半山石刻中，有一巨型榜书“西南保障”，是国民革命军十五军参谋长何宣于1935年冬天题刻，以表达抵御日寇的气概与决心。此碑刻被人们保存至今，成为桂林人们奋勇抗击日寇的不可磨灭的印迹。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2147, '桂林飞豹户外拓展俱乐部', '5590482', '地址：桂林市叠彩区大河乡南洲村', '全年 09:00-18:00；具体场次以当天景区发布为准。。', ' ', ' 桂林飞豹户外拓展俱乐部场地在灵川希宇娱乐城，里面还有很多游玩项目，有卡丁车，水上世界、游乐园等，玩cs不再单调。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2148, '超级大喇叭', '5037341', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 乘坐四人皮筏从25米高的平台出发，随着巨浪在滑道中晃动撞击，倾斜旋转，在漩涡中急速俯冲，在大喇叭里左右摇摆、滑行，在激荡的水流中，让你体会失去平衡感的同时，像被卷进龙卷风里，体验失重的滋味，在速度与惊险中尽情欢呼。 ', 'guilin28', ' ', '', '');
