INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2150, '歌仙·双月湖', '2486273', '地址：桂林市阳朔县十里画廊历村段月亮山斜对面歌仙·双月湖景区', '9:00-18:00。', ' ', ' 歌仙·双月湖景区，实景真人还原电影《刘三姐》，陪游客一起重温电影情节。周末或约上三五好友，或陪着家人孩子一起来这里了解刘三姐故事、观赏刘三姐表演、赏月亮山、月亮湖，让周末更有意义。阳朔歌仙·双月湖景区位于阳朔十里画廊历村路段（月亮山斜对面）。全场实景表演，重现电影《刘三姐》里的精彩情节！想了解刘三姐故事、观赏刘三姐表演、观赏月亮山、月亮湖，歌仙·双月湖是最佳的选择。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2151, '大滑梯', '5038341', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 登上十几米的高空平台，沿着几乎垂直的滑道，高速穿越滑道至谷底，在巨大的惯性作用下，体验自由落体的尖峰时刻，令人激动的尖叫，在失重的瞬间体验着_上天入地的快感和时空倒转的激情。 ', 'guilin28', ' ', '', '');
