INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2153, '章鱼滑梯', '5038342', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 章鱼滑梯的彩色滑管，远看就像八条舞动的飘带，与朋友一起，沿着交错的“飘带”一起水中“飞舞\"吧! ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2154, '徐村', '1837710', '地址：桂林市临桂县徐村', '8:00-21:00', ' ', ' 徐村位于桂林市临桂方向，离桂林市区约50公里，不仅拥有桂林山水的美景，远离都市的喧嚣，更有清新脱俗的田园风光。这里是桂林万亩油菜花基地，徜徉金色花海，尽情呼吸清新空气，让醇浓花香沁人心脾。“菜花间蝶也飞来，又趁暖风双去。”油油的绿衬托着油油的黄，与蝶共舞，不经意地落满一身花蕊、一片清香。盈盈招手，暖风掠过，轻轻摇曳，暮然忆起，那是儿时最纯真的记忆！自由拍照摄影，爱好摄影者不能错过的机会。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2155, '大型水寨', '5037342', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 无数盘旋而上的阶梯连接起碧波荡漾的水面和错落相交的滑道。坐上各式各样的滑道直入水中来个透心凉，或是直接来场水花四溅的战斗，让蓄满重达一吨的大水桶劈头盖脸直接浇下来，你敢不敢来一次这样无畏的冰桶大挑战 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2156, '罗山湖水上民族风情岛', '5010396', '地址：桂林市临桂县两江镇两江大道1008号', '', ' ', ' 罗山湖水上民族风情岛主要展现广西11个少数民族的历史、文化风情；近百亩的湖岛面积，奇异独特的地形地貌中，藏着优美的湖岛景致，打造成山水版的民族风情博物馆，尽显广西各民族风采。 ', 'guilin28', ' ', '', 'http://www.roshanlake.com/index.php');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2157, '高洲金龙湾休闲园', '5648364', '地址：桂林市阳朔县阳朔镇高洲村委高洲村', '', ' ', ' 高洲村有3300米长的漓江旅游休闲步道，从原来只有半米宽的泥巴路变成了近3米宽的休闲路。沿路3000多亩金桔、蓝莓、沙田柚的施肥、摘果也告别了肩挑背扛，村民的果园已成为游客进园摘果体验农情的基地。被誉为阳朔最美瀑布的虎山瀑布受到越来越多游客青睐……乡村旅游实现向漓江徒步游+乡村生态游+农家生活体验游等多元复合的经济发展模式转型升级。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2158, '螺旋组合滑梯', '5037343', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 在特别设计的双人浮圈里面，通过105米半敞半封闭式的滑道，再经过曲形管道，高速冲入下面的水池。这种极速感带来的肾.上腺素飙升，顶级超跑也不过如此! ', 'guilin28', ' ', '', '');
