INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2161, '巨兽碗', '5038343', '地址：广西省桂林市罗山湖旅游度假区玛雅水上乐园', '开放时间：09:00-22:00（周一至周日）', ' ', ' 想穿越宇宙魔幻黑洞，体验360度翻转的乐趣吗?来罗山湖玛雅水上乐园吧，这里有尺寸最大、惊险刺激的巨型大海碗巨兽碗，可以容纳整个家庭的极限挑战，从滑道中俯冲穿越，惊现紧贴在巨大的碗墙之上。当你稍觉平静，突然巨兽碗中出现一个巨大的黑洞将你猛然吸入，随后从魔幻般的黑洞中回归风平浪静的现实场景。 ', 'guilin28', ' ', '', '');
