INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2163, '阳朔梵高星空艺术馆（益田西街旗舰店）', '5633352', '地址：桂林市阳朔县西街蟠桃路益田西街（阿玛瑞酒店入口）', '', ' ', ' 这是一家以印象派色彩主义画家梵高的作品为主线的艺术馆，馆内带着超级浓厚的色彩感，基调和梵高的作品简直融为一体，仿佛来到了梵高的世界一样。馆内的4D场景视觉效果，让人仿佛身临梵高作品里一般，在这一片小小的天地中，感受着梵高的艺术魅力。你甚至可以置身于星空下，坐在屋顶，梵高的《星月夜》互动，在这烦嚣的世界里，体验这一刻的梵高和它在星空下的那个心境。馆内还有几个特别的地方，比如镜像迷宫、无限星空、呼吸森林、镜幻宫、50+大主题场景...等，让你宛如置身在梵高的梦境中，有时候像行走在浩瀚宇宙中，有时候像走在无尽的迷宫中，永远走不到尽头。 ', 'guilin28', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2164, '木龙湖山水梨苑', '2521246', '地址：桂林市区中山北路30号', '9:30-21:00', ' ', ' 流光溢彩的夜景、气势恢宏的唐宋建筑，秀美的木龙湖风光，丰富的人文古迹使木龙湖景区成为桂林市区璀璨亮丽的景区。其中的山水梨苑剧场更是桂林地域文化的完美诠释，桂剧是广西的主要剧种之一，广西汉族地方戏曲之一 。俗称桂戏或桂班戏，是用桂林方言演唱的剧种，做工细腻贴切、生动活泼，借助面部表情和身段姿态传情，注重以细腻而富于生活气息的表演手法塑造人物。 ', 'guilin28', ' ', '', '');
