INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2169, '桂林漆器传承基地', '5621420', '桂林市雁山区雁山街389号桂林园博园湿地馆', '全年 08:30-17:30。', ' ', '漆器与瓷器、丝绸一起作为中国古代三大传统手工艺，桂林漆器传承基地位于桂林园博园湿地馆，是广西壮族自治区级非物质文化遗产项目，是目前广西唯一恢复制作的珍贵文化遗产。桂林漆器传统制作技艺历史悠久，是古代岭南工艺技术文化的优秀代表。桂林古代漆器品种门类众多，但最具有地方特色的是巧妙利用自然资源的杰作——天然胎漆器：例如竹根制胎漆器、灵芝胎漆器、葫芦胎漆器、贝壳胎漆器、角胎漆器，盘藤制胎漆器、柚子皮制胎漆器等等。桂林漆器以其浓郁的地方特色，逐渐为世人所知。作品以小巧精致见长，制作要求极其严谨，每件作品均独立设计，具有千器不同面的特征，十分耗工费时，往往数年才成一器，成品精巧绝伦。', 'guilin28', '', '', '');
