INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2485, '石螺口海滩潜水体验', '2478125', '北海市海城区涠洲岛景区内涠洲岛西海岸', '', '5', '涠洲岛是中国地质年龄最年轻的火山岛，也是广西最大的海岛。有海蚀、海积及溶岩等景观，美丽的海滩，和独特的小资情调。这里冬天无寒，夏天酷暑，年平均气温23摄氏度，全岛绿树茂密，气候宜人，风光旖旎 ，堪称人间天堂，蓬莱宝岛。石螺口海滩是涠洲岛公认的潜水胜地，水下能见度为8-16米，有些地方达到25米。成群的热带鱼，美丽的珊瑚，海底世界的奥妙等你探索。专业的潜水装备和潜水教练，让你接受全面的培训，了解船潜的知识，感受最真实的海底世界。', 'beihai140', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2486, '外沙海鲜岛', '52842', '地址：北海市海城区外沙岛四川路尽头', '全天开放', '4', ' 北海外沙海鲜岛是广西地区最大的海鲜集散地和最负盛誉的海鲜餐饮区。小岛四面环海，呈带状，拥有2公里的绵长海岸线，最适宜观海。外沙岛是北海市北岸的一个长条形小沙岛，距离陆岸仅数十米。岛内主要包括两个区域：占地250亩的东区是海鲜主题乐园，包括十八家风情各异的高档海鲜酒楼、珍珠市场、干海味市场、活鲜市场、四星级酒店、艺术家村、土特产商行等；占地200亩的西区，是以运动为主题的海滨休闲度假酒店群。 ', 'beihai140', ' AAAA 景区', ' 0779-2087110 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2487, '北海港务局客运码头出海游', '2477927', '地址：北海市海城区海角路145号', '', '4.6', ' 北海海上观光游轮从北海港务局码头出发，全程约两个半小时，乘风载浪，北海景色一览无遗。船内共有三层，一层为演艺大厅，可以在此观看精彩的歌舞表演；二层为自助餐厅；三层为观光平台，可以全方位欣赏北海迷人的景色。游船环北海半岛一周，途径北海港客运码头--海警码头--红色娘子军民兵连（地角渔村）--南海舰队北海军港码头--万吨级邮轮码头--钓鱼台国宾馆北海冠岭山庄，缓缓行驶于波光粼粼的海面上，饱览海滨旖旎风光，吹着海风，踏着海浪，品着海鲜，享受疍族渔家风情。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2488, '贝壳沙滩', '1674081', '北海市海城区涠洲岛', '全天开放', '4.3', '贝壳沙滩它位于涠洲岛东北部海岸因贝壳众多而得名这里海水清澈、沙滩绵长、贝壳繁多，非常适合散步和拾贝 由于其独特的地理位置东部的贝壳沙滩是观日出的绝佳地方这里游客稀少，因此保留了原生态景色也吸引了许多喜欢清静的游人前往', 'beihai140', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2489, '冠头岭国家森林公园赶海体验', '2484342', '地址：北海市银海区西尽端冠头岭国家森林公园', '', '3.6', ' 冠头岭国家森林公园坐落于北海市区最西端，是一处既有森林又有海蚀礁石群的景区，景区里的主峰望楼岭海拔120米，是北海市区最高点，登上山顶可远眺周边海岸风光。这里的山岭蜿蜒起伏，仿佛一条青龙横跨在海面。曾经这里是海防要地，如今也是海景胜地，来这里游玩，赶海是一项不可错过体验活动。在这里可以体验沙滩拾贝、挖螺、挖海蟹、拉网捕鱼等，还可以乘坐沙滩车，或者坐快艇出海，饱览海景，与大海亲民接触，揭开大海的神秘面纱，发掘它的独特魅力。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2490, '南湾', '1674648', '地址：北海市海城区涠洲镇', '全天开放', '4.4', ' 南湾是整个岛的最南端，是岛上最繁华、人流最密集的地方，吃住极为方便。 如果在岛上时间不是太宽裕，建议选择居住南湾。这里骑电动去岛西景点全部10分钟内，去东边的天主教堂也不过20分钟。如果选择抄近路走，15分钟到达五彩滩。在南湾街上遍布着大大小小百余家门店商铺，既有土著的岛民，也有外地人在这里开的店。南湾最方便的还是海鲜市场，岛上唯一一个海鲜市场，岛上品种很多，价格也比较便宜。 ', 'beihai140', ' ', ' 0779-6016278 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2491, '冠头岭国家森林公园', '9424', '地址：北海市银海区海景大道尽头', '全天开放', '3.9', ' 冠头岭国家森林公园坐落于北海市区西端，是一处既有森林又有海蚀礁石群的景区，游客可以在礁石海滩漫步，欣赏日出日落美景。景区里的主峰望楼岭海拔120米，是北海市区至高点，登上山顶可远眺周边海岸风光。冠头岭公园面积不大，景点也并不多，这里的海蚀礁石群是主要景观，漫步在海滩上，可看到大小不一的礁石，与冠头岭西麓那块被海水侵蚀而成的巨大陡壁形成错落有致的独特美景。礁石滩上有名的便是“海枯石烂”礁石龟，这是冠头岭的标志性景点，吸引着人们来留影。走过海滩，便可沿着步道登山，由于山体不高，登上去也十分轻松。到了山顶的观景平台，便可一览周围海岸美景，附近还有一座普度寺，也可以去拜访一下。 ', 'beihai140', ' ', ' 0779-3883863 ', '');
