INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2497, '月亮广场', '1418243', '地址：北海市海城区涠洲岛景区鳄鱼山景区内', '包含于涠洲岛风景区，景区开放时间5月1日-10月31日8:00-18:00；11月1日-次年4月30日8:00-17:30，景点开放时间详见景区现场公示。', '4.3', ' 月亮广场是鳄鱼山景区内的一处观景平台。月亮广场虽然很小，甚至称不上真正的广场，但这里的观海视野非常开阔，站在此处可以把整个南湾尽收眼底，加上广场本身是个漂亮的圆形无边平台，更成为游客摆拍的绝佳之处。 ', 'beihai140', ' ', ' 0779-6016278 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2498, '北海环岛游', '137255', '北海市海城区海角路230号北海港客运站', '全年 09:00-15:30；开航时间：上午9:00、11:00，下午13:30、15:30，请提前30分钟到达景区取票。', '4.1', '乘风载浪、滨海景色、一览无遗！新邮轮，北海环岛游全新升级，高端大气上档次的游轮邀您共赏北海风光！ 可载游客150人的“北港邮21、22号”游轮，长35米，宽7.7米，是一艘集观光、餐饮等多功能于一体的国内海岸航线最豪华的游船之一。该船外型同是采用现代化观光玻璃钢材质，船上配有商务自助餐台、会议投影仪、豪华音响和中央空调。其结构共分为三层：一层为演艺大厅，二层为自助餐厅，三层为观光平台。 午或下午（时间提前来电预订）于北海港务局码头乘船出海，在约两个半小时的航行中，游船环将北海半岛一周，途径北海港客运码头海警码头红色娘子军民兵连（地角渔村）南海舰队北海军港码头万吨级邮轮码头钓鱼台国宾馆北海冠岭山庄，缓缓行驶于波光粼粼的海面上，饱览海滨旖旎风光的游人吹着海风，踏着海浪，品着海鲜，享受疍族的渔家风情。  ', 'beihai140', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2499, '北部湾一号', '1418217', '地址：北海市海城区茶亭路49号（上海路与茶亭路交汇往东200米）', '全天开放', '4.4', ' 北部湾1号项目位于\"广西海景第一路\"北海海景大道南侧，北临海景大道、南至茶亭路、东西在湖北路与上海路之间，毗邻国家A4级景点“北海海底世界公园”、海滨公园及五星级香格里拉大饭店、富丽华大酒店，区域内交通便利，景观优美，环境幽雅。北部湾一号，由国际建筑大师马岩松先生设计，是位于广西北海市的西北部的艺术建筑群，是北海的地标性建筑。它的独特之处就是把桂林山水的外形搬到了北海，取意“山水城市”，意在将自然中的山水意境引入城市，在城市中造就山水的意境。 ', 'beihai140', ' ', ' 0779-2066733 ', '');
