INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2560, '国家地质博物馆', '3197929', '涠洲岛', '5月1日-10月31日 08:00-18:00；11月1日-次年4月30日 08:00-17:30；以上披露时间为上岛门票时间，最晚开放时间视当天最晚航班而定。。', '4', '北海涠洲岛火山国家地质公园占地面积26.88平方公里，是中国最大最年轻的火山岛，岛上地质、地貌多样，海洋生物繁多是进行科研、教育、考察的良好场所。 公园有火山地质博物馆，馆内设有陈列室、标本室、展示厅、录象厅等科普活动场所，2005年以来地质公园通过图片、标本展示、录象播放和发放资料等形式向游人、岛民、师生展示了海岛特殊的地质、地貌和环境风采，宣传了保护环境、保护海洋的意识，取得了良好的效果，建立了一个寓科学于娱乐之中，寄科普知识于观光之上，集高新科技于一体的科普教育基地。 岛上唯一官方景区讲解员，正规持证，中英语种可选，实地讲解经验丰富，熟悉岛上火山地质地貌景观和人文历史，是一支有经验、年轻富有活力的导游队伍，为您提供优质全面的讲解服务。  ', 'beihai140', '', '', '');
