INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2582, '北海贝雕博物馆', '1913585', '北海市云南北路西九巷6号', '全年 09:00-17:30；16:30停止门票发放及停止进入场馆。。', '4.2', '北海贝雕博物馆是国家AAA级旅游景区，位于广西北海市云藏路6号，是全国首家以贝雕为主题的博物馆。 贝雕馆以“传承北海贝雕技艺、弘扬中华传统文化”为宗旨，以观光旅游为中心，贝的历史和贝雕技艺文化为主轴线。 特设贝雕历史文化起源区、生产体验区、实物产品区、镇馆之宝区等区域，呼吁社会各界积极参与到传统手工艺的传承与保护中来。 为游客们更好的体验北海贝雕的历史发展，贝雕博物馆为参观团队配有讲解员全程讲解,并特设贝雕制作体验台，感受贝雕制作过程。 ', 'beihai140', '', '', '');
