INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2584, '银滩直升机观光', '2478039', '地址：北海市银海区银滩四号路', '9:00-17:00，具体开放时间详见景区公示。', ' ', ' 北海银滩面积超过大连、烟台、青岛、厦门和北戴河海滨浴场沙滩的总和，而平均坡度仅为0.05。沙滩由石英砂堆积而成，在阳光的照射下，洁白、细腻的沙滩会泛出银光，故称银滩，北海银滩以“滩长平、沙细白、水温净、浪柔软、无鲨鱼”等特点闻名。乘坐直升机低空飞行，从空中俯瞰魅力北海，同时享受冲上蓝天和遥望大海的美妙体验，可将碧海蓝天和银滩的无限风光尽收眼底。此外，还可以同时在高空欣赏红树林、园博园、规划馆、冠头岭、侨港、海滩公园等周边景点，多条线路带你领略360度北海风光。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2585, '北海银滩拖伞帆船体验中心', '5589495', '北海市银海区银滩正门海边西边', '全年 10:00-17:00。', ' ', '海上拖伞因其乘坐危险系数小、难度低受到广大游客欢迎。海上拖伞腾空而起的快意，耳边呼啸而过的疾风，骤然上升的速度和被牵引的力量，似乎让你长出一双轻灵的翅膀，载满无限的希冀，向着蓝天和大海进发……居高临下饱览银滩和大海美景的惬意以及沐风巡游的畅快定会令您耳目一新。 这是一项极富刺激性的海上运动。拖伞从起飞到降落，一切动作均在拖伞船上完成。起飞前工作人员会为你穿好降落伞，系好拖绳，随着拖伞船的加速，穿着降落伞的你会因空气阻力而升空，降落伞在空中绽开, 逐渐升高，最高可达20米，可单人飞或双人飞，并可蜻蜓点水一至二次。 在拖伞船上自由起落，在浩瀚无限的天空中随意翱翔，把自己融入碧海蓝天之间，像鸟儿一样在天空中翱翔，此时的心也在飞翔。从空中俯瞰大海和银滩，景色更加的迷人，融入大自然，使您烦恼尽抛，心旷神怡。给您一次一生无憾的北海银滩海上飞翔之旅。 ', 'beihai140', '', '', '');
