INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2588, '梦幻水母馆', '5623378', '地址：北海市海城区茶亭路27号北海海底世界景区三楼', '', ' ', '  梦幻水母馆内38个创意展缸，展示38种来自世界各大洋的近6000只水母，其中超过60%的水母品种是由该馆自行繁殖的。梦幻水母馆充分利用光学镜墙原理，配合变化多彩的特效灯光，使整个水母主题馆宛如童话世界一般神奇美丽，如真似幻、虚实互动。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2589, '金滩', '5009397', '地址：北海海景大道', '', '4', ' 在北海知名的北海老街附近有一个和东兴金滩重名的海滩，处于北海市的海景大道，这就是北海金滩。 北海金滩没有东兴金滩的名气，更没有北海银滩的盛名。但她有未开发的朴实安静的气质。银滩的沙子是银色的、细腻的，而北海金滩的沙子则是粗犷、金色的，在阳光下金光闪闪。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2590, '海底隧道', '5622401', '地址：北海市海城区茶亭路27号北海海滨公园内北海海底世界景区内', '', ' ', ' 让人们置身于绚丽多彩、变幻无穷的海底，头顶着蔚蓝色的海水，脚踏着软软的海床，身边簇拥着千姿百态的鱼儿，您仿佛来到了童话般的世界。北海海底世界的海底隧道全长近百米米, 分为三个部分，全部采用亚克力透明超强压玻璃，玻璃厚度为9厘米。由于玻璃的弧度原因，您所看到的鱼类及景物比实际缩小了三分之一。首先进入的是第一隧道。这里有黑鳐、美国红鱼、大苏眉、青衣、胡椒鲷、鲈鲀、炮弹鱼等几百种鱼类，以及海底铁树、珊瑚礁等。水下还静卧着二次大战中被击沉的舰艇，这是仿珍珠港事件中沉没在海底的美国“亚利桑那”号而造。大家知道吗？北海是古代海上丝绸之路的起发港之一，我们的先人在这里扬帆起航，将中国的古代文明带向世界各国，如果您仔细看的话，还能找到古代海上丝绸之路上遗留下的残瓷瓦砾。 ', 'beihai140', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2591, '涠洲岛南湾海上运动基地', '5598344', '北海市海城区银滩旅游区18号', '全年 09:00-17:30。', ' ', '南湾海上运动基地是全岛一家综合性海上运动基地，在这里您可以体验一站式的服务和多样的海上娱乐项目，刺激帆船、休闲游艇、魅力涠洲/斜阳环岛游、探索海底海豚潜水、让人狂飙肾上腺素的拖伞和漂移艇等等。 动可当驰骋大海的航海家，欣赏涠洲岛美景，又可如海豚精灵般畅游，探索海底奥秘； 静可远离城市的喧嚣，出海垂钓，看日出日落，亦或是好友相聚，惬意巡航，都能让您尽兴而归。 涠洲岛景区旅游公交车，由新涠公交公司进行规范管理、集中经营。采用纯电动车辆运营，绿色环保，安全可靠，方便快捷,服务优质。线路通达岛上各大 景点、村庄、酒店、农家乐。 ', 'beihai140', '', '', '');
