INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2594, '极地企鹅馆', '5620394', '地址：北海市海城区茶亭路27号北海海滨公园内北海海底世界景区三楼', '', ' ', ' 这些极地萌宝来到这里，就连吃饭游泳这些日常行为，都能把吃瓜群众萌的不要不要的。如果被这些小萌宝亲一口或者合张影，那简直心都要萌化了！为了给企鹅们打造一个舒适的生活环境，营造出逼真的极地冰雪世界， 海底世界景区配套相关高端的设备设施，运用现代海洋馆先进的维生系统，严控室温和水温、湿度等环境指标，对企鹅馆进行充分消毒杀菌，还根据企鹅的生活习性，提供专用的食物。企鹅的日常养护将依照非常科学、严谨的程序操作，让企鹅宝宝健康快乐地在北海海底世界生活。 ', 'beihai140', ' ', '', '');
