INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2600, '碧水云天帆船俱乐部', '5629420', '北海市海城区涠洲岛西角89号', '全年 08:00-20:00。', ' ', '北海碧水云天帆船俱乐部隶属于北海碧水云天体育文化传播有限公司，俱乐部现拥有hobie级别帆船，帆板，皮划艇，海上沙发，海上浮毯，海上冲浪等海上运动器械。拥有海上救生员，帆船驾驶员，潜水教练员，引导员数名。 俱乐部位于美丽的涠洲岛蓝桥向南100米的沙滩，因地理位置特殊，南靠码头，北靠蓝桥，形成了独具一格的港湾风貌，无风无浪，海水清澈见底，水中珊瑚丛生，水中生物嬉戏，海底风光一览无遗。该片沙滩又因全滩无乱石，沙质细软等特点。故被誉为全岛适合的出海游玩沙滩。 ', 'beihai140', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2601, '红树林西门赶海', '5630405', '北海市海景大道下村海边大门口（距离金海湾红树林风景区门口前面一公里处）', '全年 06:00-18:00。', ' ', '红树林西门赶海一个原始的红树林赶海圣地，每天根据潮水的规律，在退潮的时候穿越红树林到海滩上挖贝壳、挖竹节螺、挖沙虫、抓沙蟹、捡生蚝等，回家品尝亲自打捞的海鲜。 这将是孩子的一堂海洋知识课，更是一次别样的旅游体验。赶海可以说是一项体力活，也可以说是一个拍照圣地，按您喜欢的旅游方式体验本地赶海居民的生活。 ', 'beihai140', '', '', '');
