INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2815, '广西南宁奔腾马场', '1489037', '地址：南宁市兴宁区邕武路银林山庄往前十公里处生力军山庄', '10:00-18:00。', '4.2', ' 奔腾马术俱乐部成立于2013年6月，马场占地约15亩，拥有完善的300米跑道，马房和烧烤场等游乐设施，具有特色的户外野骑路线，令您骑乘于山间小路上，既刺激又能有驾驭感。马场共有马匹35匹，依桥傍水，在这里骑马出游，必定会使你远离城市的喧闹。奔腾骑马场设施安全、服务周到。是您假日休闲娱乐的理想场所。马场拥有优质的国产山东西南马，还有品种优良的进口纯马血马，半血马供专业的马术爱好者骑乘，另外配宽敞明亮的马房及完善的配套设施，资深的马术教练提供系统的马术培训课程，提供精良的专业防护装备保护骑乘者安全。俱乐部建设了宽敞、舒适的会所及酒店供会员娱乐休息。奔腾马场是广西唯一一家有场地速度赛又有野骑的马场。密林穿行，观看沿途的风景，策马啸西风，领略大自然的风光。坐在马上，好不快哉！涉水骑行，沿着溪流流向，水最深达1米，风景优美，线路刺激，体验骑马的无限乐趣！林荫小道飞奔，淋漓尽致的狂奔，让马尽情的奔跑，让你体现马上的速度与激情！来回大约50分钟左右，全程教练随行监控，随时给您提供帮助。 ', 'nanning166', ' ', '', '');
