INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2820, '广西体育中心', '1674000', '地址：南宁市良庆区五象新区核心区五象大道南侧', '演出场次：5月30日20:00。', '4.1', ' 广西体育中心工程总占地1143亩，总建筑面积达19.8万平方米。该工程位于五象新区核心区五象大道南侧，工程包括体育场、体育馆、游泳跳水馆以及现代化的网球中心。体育中心主体育场建筑采用具有飘逸感的多曲线屋面，充分呈现出现代气息。 广西体育中心一期工程主体育场封顶，银色的主体建筑气势磅礴，主体育场顶部造型犹如两片飘动的“绿叶”，造型优美而新颖。 网球中心、游泳馆、体育馆和主体育场的体积、位置，正好排成一个由小到大的序列，仿佛是“绿叶”的成长过程。因此设计采用隐喻的手法，以网球中心隐喻种子，游泳馆隐喻叶芽，体育馆隐喻嫩叶，主体育场隐喻飘叶，去描绘“绿叶”成长的生态过程，也以此构成了南宁这个以绿色为荣的城市的生动故事。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2821, '江南公园儿童游乐园', '5012390', '南宁市江南区壮锦大道17号', '9:00-21:00', ' ', '江南公园儿童游乐园位于南宁市江南区壮锦大道17号江南公园内，公园主要由“一山三桥五湖”构成：“一山”是文峰山；“三桥”是状元桥、映月桥、清流桥；“五湖”是町流湖、映流湖、沧流湖、涓流湖和清流湖。 江南公园儿童游乐园,这里是让孩子天性尽情释放的战场！放肆玩耍尽情嗨！把玩遍乐园每个项目当做“终极目标”！带着小朋友勇闯每个关卡吧！ ', 'nanning166', '', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2822, '太空嘉年华3D数字太空体验馆（南宁站）', '4379367', '地址：南宁市青秀区民族大道136号万象城L1层中庭旁', '10:00-22:00，2018年3月31日-2018年6月15日。', '5', ' 太空嘉年华3D数字太空体验馆（南宁站）将太空嘉年华的LOGO形象及各种卡特星球的形象通过裸眼3D技术，投影到立体形象墙上，让观众一进入展厅就眼睛一亮，也是观众拍照留恋的最佳区域之一。通过雷达互动装置等设备，形象生动地演绎了从地球出发，到地月系、太阳系、银河系直至多重宇宙的过程。当观众把足够多的手放在红外感应的互动桌上，便启动了宇宙大爆炸，之后墙面四个投影画面联动，演绎宇宙大爆炸由一个致密、高温的奇点，经过抛射、膨胀、冷却的过程，最终形成我们今天的宇宙。屹立在观众眼前的是一座座伟大的科学家的雕塑，当大屏幕上的影片讲述他们对人类做出卓越贡献的事迹的时候，通过3Dmapping技术使他们“活了”过来！雕塑上的动态人物影像，就好像真的在和我们面对面讲述他们探索宇宙的故事！通过裸眼3D巨幅互动投影，观众可以通过雷达感应亲手打开火箭的外壳，火箭的结构和功能一目了然，逃逸塔、整流罩、推进舱、返回舱、轨道舱以及一级二级发动机在观众的眼前组合、拼接、运转，最终飞向太空。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2823, '龙门水都水上乐园', '4383370', '地址：南宁市西乡塘区振兴路那秧坡附近龙门水都景区内', '暂时停业，预计2019年6月1日营业。6月-11月10:00-18:00', '5', ' 龙门水都水上乐园挑选目前国际流行的水上娱乐项目，引进国外先进技术和设备，有高速滑道、组合滑道、儿童戏水池、按摩池等。 ', 'nanning166', ' ', ' 0771-5600666 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2824, '广西南宁绿野马场', '1489025', '地址：南宁市邕武路林科院后（公交车212路终点站，林科院菜市左转弯直进到后面树林）。', '9:00-18:00', '4.5', ' 绿野马场位于邕武公路广西林科院后，马场绿树环绕，空气清新，彼临\"老虎岭水库\"，距离市区18千米，约40分种车程。绿野马场是南宁最早经营的一家农家乐马场，在南宁骑马界有一定良好的口碑。是邕城休闲游的好去处，绿野马场现有广西马十多匹，活动项目有野外骑马、射箭、农家菜，烧烤、棋牌麻将、钓鱼、郊游、定向越野等活动。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2825, '广西艺术文化中心', '4387378', '地址：南宁市良庆区龙堤路25号', '20:00-21:00。', '4.8', ' 广西文化艺术中心位于广西南宁市良庆区龙堤路25号，南宁大桥以南，北临邕江，建筑面积9万平方米。广西文化艺术中心经常有艺术演出活动，是广西重大文化性公共设施和最高级别的艺术表演中心，是您休闲放松的好去处。 ', 'nanning166', ' ', ' 0771-8053288 ', '');
