INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2827, '南宁冰雪世界', '4711349', '南宁市良堤路6号南宁万达茂2号门广场', '全年 10:00-22:00。', '4.3', '阿狸欢乐王国是一个国内知名漫IP打造的主题乐园，乐园分为梦之小镇、梦之城堡和冰雪世界三大展区，项目亮点：淋不湿身的秘境雨屋、2米高的玫瑰花园、梦幻有趣的童话小镇、星光游乐场、零下10℃的冰雪世界。 绚丽的冰雪世界，水晶般的冰雕艺术品，在南方让您不去哈尔滨就能体验到北国冰雕穿越时空的幻想绚丽！里面有360度冰上过山车、冰上碰碰车、三层滚筒滑梯、百米急速滑道等互动项目，除此之外，展馆内共分为魔法森林、回声冰屋、梦幻隧道、糖果世界十大主题展区，让你近距离欣赏冰雕大师的鬼斧神工。  ', 'nanning166', '', '', '');
