INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2860, '金沙湖', '2488400', '地址：南宁市西乡塘区邕隆路268号', '9:00-17:00', '2.6', ' 南宁金沙湖风景区位于南宁市西乡塘区大学西路尾，景区占地2000多亩(其中湖水面积为1000余亩,山林面积1000余亩)，湖中大小岛屿18座，有白鹭等珍稀鸟类25种，在湖中鸟岛上栖息的白鹭及灰鹭有2000多只，数百羽天鹅落户金沙湖。这里空气清新，景色宜人，气候温和，鸟语花香，林湖相依，是鸟类栖息、生活、繁衍的好地方，是“鸟的天堂”。景区内现有宾馆、餐馆、球馆和会议室等会议培训设施，以及钓鱼、户外拓展、烧烤、游船、采摘、卡拉OK、儿童充气城堡、室内外汽排球、羽毛球、棋牌室等健身休闲娱乐项目及设施。 ', 'nanning166', ' ', ' 0771-3250071 ', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2861, '中国—东盟友谊馆', '5638352', '地址：南宁市邕宁区蒲兴大道20号南宁园博园内', '', '5', ' 中国—东盟友谊馆建筑面积7280平方米，高23.9米。 该建筑以广西的风雨桥为原型，弧形向内同心叠合，寓意“手拉手，心连心”。馆内设有金色大厅及东盟十国展厅，十国展厅按国际惯例以国家英文名字首字母排序，分别为文莱、東埔寨、印尼、老挝、马来西亚、缅甸、菲律宾、新加坡、 泰国、越南。展厅相对独立，又彼此联系。展陈以“花之绽放”为主题，展示东盟各国浓郁的特色文化，表达睦邻友好、合作共赢之意。 ', 'nanning166', ' ', '', '');
