INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2870, '银林山庄', '2121824', '地址：邕武路23号', '全天开放', '2.5', ' 银林山庄是集休闲度假、健身娱乐、商务会议、学习培训于一体的旅游区。山庄占地面积约6.7万平方米，设有高、中档客房10幢，360个床位;5个可容纳50——300人的会议室;3个大型中餐厅，可同时容纳800人就餐。还有体育馆、游泳池、网球场、保龄球、羽毛球馆、汽排球馆、歌舞厅，牌艺室，茶馆、烧烤场等健身娱乐设施。山庄四周环绕着百亩果园，农田池塘、绿树葱郁，一派田园风光。山庄内鸟语花香，曲径通幽，小桥流水穿梭在亭台楼阁之间，许多罕见的奇花珍木，吸引众多游人慕名而来。山庄一隅的苏州园林式建筑——银馨园别具一格，古香古色的四合院，雅致的碑林画舫，犹如都市里的世外桃源，使得游客留连忘返。 ', 'nanning166', ' ', ' 0771-3332223,0771-2022228,0771-2022218 ', 'http://www.gxylsz.com/');
