INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2960, '柯基跑跑车', '5070349', '地址：南宁市青秀区青环路66号南宁方特东盟神画内', '9:30-18:30', ' ', ' 柯基跑跑车是孩子王喜爱的爬山车项目，可以驾驶各式各样、色彩斑斓的动物造型小汽车翻山越岭、追逐嬉戏，变身神气的小司机。小动物们列队盘旋在高低起伏的立交跑道上，时高时低，犹如穿越时空，时而翻山越岭，你追我赶，热闹非凡。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2961, '空中飞舞', '5070350', '地址：南宁市青秀区青环路66号南宁方特东盟神画内', '9:30-18:30', ' ', ' 优美、轻快的旋律响起，在转盘及离心力的作用下，上下飞旋，时而像在蓝天中翱翔，时而像在海上冲浪，能感受腾云驾雾的快感。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2962, '雨林探险', '5071358', '地址：南宁市青秀区青环路66号南宁方特东盟神画内', '9:30-18:30', ' ', ' 乘坐漂流船爬上高坡，穿过热带雨林，在急缓交错的河道上感受沿途的风景。当达到15米高处时，转过弯道随着激流飞滑而下，劈波斩浪刹那间，击起朵朵浪花，有惊无险，在炎炎夏日体验清凉一夏。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2963, '桂花园', '4376392', '地址：南宁市青秀区凤岭南路6-6号青秀山风景区内', '6:00-24:00', ' ', ' 桂花园位于景区北坡山谷，西面毗邻兰园、北门区，东侧为樱花园。桂花园占地240多亩，园内有“双桂当庭”、“金玉满堂”等景点，是一个以桂花造园造景、展示桂花植物品种、桂花主题文化为特色的生态专类园。园内主要种植有“朱砂桂”、“大花金桂”、“月月红”等40多个品种约万株桂花。10月至11月中旬迎来最佳赏桂期，满园桂子飘香，置身其中，阵阵馥郁清香袭来，沁人心脾，这或许便是一年当中最甜美的季节。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2964, '疯狂巴士', '5071359', '地址：南宁市青秀区青环路66号南宁方特东盟神画内', '9:30-18:30', ' ', ' 疯狂巴士等待你和家人乘坐在巴士上，体验开心一刻。但不知什么时候，巴士就会像它的名字一样“疯狂”起来 ，带你体验不一样的乘车体验。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (2965, '巨兽碗', '5039345', '地址：广西南宁市青秀区云景路25号凤岭儿童公园内', '开放时间：10:00-17:00（周一至周日）', ' ', ' 从平台19米高的塔端开始急驰，在完全密封的水槽完成一个循环后，游客会进入到开放式的直行部分并急驰到末端。非常适合喜欢挑战和刺激的年轻人。 ', 'nanning166', ' ', '', '');
