INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3015, '南宁国际民歌艺术节', '1739191', '地址：南宁市五象大道669号广西体育中心', '', ' ', ' 南宁国际民歌艺术节的前身是创办于1993年广西国际民歌节，1999年正式改为现名，它由国家文化部社会文化图书馆司、国家民委文化宣传司和南宁市人民政府联合主办，是一个融文化、旅游、经贸为一体的综合性大型节庆活动。该艺术节一年举办一次，举办地点定于广西壮族自治区首府南宁。从2004年起，南宁国际民歌艺术节在连续服务九届中国-东盟博览会的实践中，成功开启了中国与东盟文化合作的新篇章，也成为广西与全国各地、世界各地文化交流的重要平台。 为把民歌发扬光大，从1993年起广西开始举办民歌节。人们在民歌节上以歌传情，以歌会友，共同抒发对美好生活的向往和热爱。民歌成了飞架于广西各民族与全国各兄弟民族及世界民族之间的彩虹。 1999年11月，首届南宁国际民歌艺术节成功举办了大型广场文艺晚会《大地飞歌》、中国(南宁)民族服饰博览会、广西民族风情展演等系列文化活动。南宁国际民歌艺术节因此一炮走红。它以浓郁的民族风情、开阔的国际视野和强劲的现代气息，赢得了社会各界人士的赞誉。 南宁国际民歌艺术节的宗旨是继承和弘扬壮族人民的文化艺术，加强与世界各民族文化的交流和发展。艺术 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3016, '大摆锤', '5544467', '地址：南宁市西乡塘区大学东路73号南宁动物园内', '', ' ', ' 游客坐在圆形座舱中，面向外，以压肩作为安全束缚，配以安全带作为二次保险。旋转的同时，悬挂座舱的主轴在电机的驱动下做单摆运动。大摆锤的运行可以使游客惊心动魄。 ', 'nanning166', ' ', '', '');
