INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3028, '七彩湖', '5638351', '地址：南宁市邕宁区蒲兴大道20号南宁园博园内', '', ' ', ' 七彩湖景区位于园博园东南部，区域内有7个废弃的采石矿坑。由于曾经的开山采石活动使用爆破开采式，开采面破碎，山体和植被破坏严重。加之地处复杂熔岩地质区，停采后部分矿坑水位持续变化。矿区呈现出破败荒凉，混乱无序的景象。七彩湖景区以场地上分布的7个矿坑作为艺术、生态、游憩等多重体验的载体，依据每个矿坑的特点采用不同的生态修复措施，改造成为景观丰富，独树一帜的矿坑花园区。7个矿坑花园包括了险峻壮观的卧虎潭，展现水上森林和瀑布美景的飞龙湖；隐秘安静的双子塘；追寻工业记忆和园艺之美的寻芳台；展现自然岩石及岩生植物景观的石趣园；感受自然野趣、欣赏崖壁景观的探幽谷；体验山水风韵和禅意唯美的落霞池。架设在原有灌渠之上的木栈道提供了独特的游览路线和观赏角度。 ', 'nanning166', ' ', '', '');
INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3029, '罗汉松园', '5636343', '地址：南宁市邕宁区蒲兴大道20号南宁园博园内', '', ' ', ' 罗汉松园面积约16公顷，位于园博园主入口，取青松迎客之意，为入园第一景。罗汉松别名土杉，属常绿针叶乔木，为国家二级保护植物。一期工程载植罗汉松35个品种340余株，是一个广西本土罗汉松专类园，也是一个与矿坑生态修复工程结合建设的罗汉松园，是一个中国写意山水园林的新实践。园内有松林、山溪、叠瀑，峡谷、峭壁、草甸，主要景点为古柯留客，林下寻芳，松溪别院，明月山溪、集珍谷、仙柏台，披霞亭，俯涧、戏鱼渊、观鱼槛、山外云，白玉坡等。 ', 'nanning166', ' ', '', '');
