INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3031, '清泉阁', '5638350', '地址：南宁市邕宁区蒲兴大道20号南宁园博园内', '', ' ', ' 清泉阁共9层，阁高75米，共9层，最大观景平台230平方米，由中国工程院院士崔愷领衔设计。原型源自侗族鼓楼，融入密檐和南宁首字母\"N”等特征，通过艺术化造型创作，成为园博园标志性建筑。建筑呈六边形布局，沿用传统的搭建方式，既体现出少数民族建筑的特点，又不失国际化的特征。登阁望远，与青秀山龙象塔形成对景，园博园壮丽美景尽收眼底。 ', 'nanning166', ' ', '', '');
