INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3044, '非洲祖鲁音乐舞蹈剧《INALA》', '77635678', '地址：广西文化艺术中心（保利 ）大剧院南宁市良庆区龙堤路25号', '2019年12月10日 周二 20:00,2019年12月11日 周三 20:00,2019年12月12日 周四 20:00', ' ', ' INALA在南非祖鲁语中意味着\"丰收\"和\"丰厚的美意\"。这出歌舞剧糅合了南非文化和西方文化，将古老的祖鲁舞与经典芭蕾、现代舞结合在一起。极 具感染力的非洲音乐，澎湃激昂的节奏、层次丰富的韵律、舞者刚劲有力的舞姿，直击每一位现场观众的心灵，营造身临其境之感，仿佛穿越到灼热阳光、繁茂雨林的非洲。 INALA 由Ella Spira 对非洲音乐的热爱应运而生。长久以来，Ella怀揣着探寻非洲音乐社会价值的雄心。2009年通过圈内友人的介绍，Ella和Pietra认识了Ladysmith Black Mambazo 乐团（以下简称LBM），双方同时看到了新型文化表演的契机。 ACT ONE 上半场 (约40分钟) 1. SIYOPHUMELELA 必须战胜所有的困难 2. ASIJABULE 痛苦的时光已经结束，让我们应声庆祝吧！ 3. HAYI BO SISEBENZA KANZIMA 我们辛勤劳作 4. WONGILINDA NG\'FIKA 别在我背后说我坏 ', 'nanning166', ' ', '', '');
