INSERT INTO `t_sight`(`sight_id`, `sight_name`, `sight_url_id`, `address`, `open_time`, `score`, `introduction`, `place_url_id`, `rank`, `phone`, `website`) VALUES (3121, '【南宁】家庭亲子音乐剧《凤凰阿佳》', '78393490', '广西文化艺术中心（保利 ）大剧院南宁市良庆区龙堤路25号', '2020年01月11日 周六 20:00', ' ', '《凤凰阿佳》讲述了在一个黄水肆虐的时代，苍临部的狮王苏安猊带领着部民们迁徙，金天国公主凤凰阿佳引领大家躲过泥石流，来到了一片美丽的森林，动物们在此处重建家园。 但却遭遇狡猾的火山神殷其雷的重重阻碍，施难使得部民们不再信任阿佳，乃至驱逐阿佳，也使得火山再次爆发…….面对熊熊烈火，以及朋友的背叛，当所有人误会她驱逐她时，依然坚守承诺，以自己的生命拯救遭遇灭顶之灾的苍临部，并在烈火中重获新生。一次性满足家庭教育中对于孩子们音乐和戏剧的双重气质塑造，更为全家人带来欢快而富有教育意义的观剧体验。', 'nanning166', '', '', '');
